import React, { useState, useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";
import { useParams } from "react-router";
import { callApi } from "./utils";
import { getCartByUser } from "../api";
const APIURL = `http://localhost:3000/api`;
const REACT_APP_API_URL = process.env;

const Login = ({
  useer,
  token,
  userId,
  setUserId,
  setLoggedin,
  loggedIn,
  setToken,
  setUser,
  setIsAdmin,
  setCartItems,
}) => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [passwordConfirm, setPasswordConfirm] = useState("");
  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");
  const [email, setEmail] = useState("");
  const [isMatched, setIsMatched] = useState(false);

  const params = useParams();

  let navigate = useNavigate();

  function logIn(resp) {

    if (resp) {
      setUserId(resp.user.id);
      setIsAdmin(resp.user.isAdmin)
      setToken(resp.token);
      localStorage.setItem("token", resp.token);
      if (resp.token == "") {
        setLoggedin(false);
      } else {
        setLoggedin(true);
        navigate("/products");
      }
    }
  }

  async function loginRoutine() {
    try {
      const resp = await fetch(`${APIURL}/users/${params.method}`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          firstName,
          lastName,
          email,
          username,
          password,
        }),
      });

      const data = await resp.json();

      setUser(data.user);
      logIn(data);
    } catch (error) {
      console.error(error);
    }
  }

  if (loggedIn === false) {
    return (

      <>
      <div className="">
          <label>Login</label>
        <form
        className="title"
        onSubmit={async (event) => {
          event.preventDefault();
          if (params.method !== "register") {
            loginRoutine();
          } else if (password === passwordConfirm) {
            setIsMatched(false);
            loginRoutine();
          } else {
            setIsMatched(true);
          }
        } }
      >
        {params.method === "login" ? (
          <><><><input
            type="text"
            placeholder="username"
            value={username}
            onChange={(event) => setUsername(event.target.value)}
          ></input><br></br><input
            type="password"
            placeholder="password"
            value={password}
            onChange={(event) => setPassword(event.target.value)}
          ></input></><button type="submit" disabled={password.length < 8}>
              Submit
            </button></><Link to="/api/users/register">Register</Link></>
        ) : null}
      </form>
      </div>
          <form
            className="title"
            onSubmit={async (event) => {
              event.preventDefault();
              if (params.method !== "register") {
                loginRoutine();
              } else if (password === passwordConfirm) {
                setIsMatched(false);
                loginRoutine();
              } else {
                setIsMatched(true);
              }
            } }
          >
            {params.method === "register" ? (
              <><><input
              type="text"
              placeholder="first name"
              value={firstName}
              onChange={(event) => setFirstName(event.target.value)}
            ></input><br></br><input
              type="text"
              placeholder="last name"
              value={lastName}
              onChange={(event) => setLastName(event.target.value)}
            ></input><br></br><input
              type="text"
              placeholder="E-mail"
              value={email}
              onChange={(event) => setEmail(event.target.value)}
            ></input><br></br><input
              type="text"
              placeholder="username"
              value={username}
              onChange={(event) => setUsername(event.target.value)}
            ></input><br></br><input
              type="password"
              placeholder="password"
              value={password}
              onChange={(event) => setPassword(event.target.value)}
            ></input><input
              type="password"
              placeholder="password"
              value={passwordConfirm}
              onChange={(event) => setPasswordConfirm(event.target.value)}
            ></input></><button type="submit" disabled={password.length < 8}>
                Submit
              </button></>
              ) : null}

          </form>

        </>


    );
  } else {
    return (
      <>
        <h1>Welcome</h1>
      </>
    );
  }
};

export default Login;
