import React, { useState, useEffect } from "react";

import { Routes, Route, Link, useNavigate } from "react-router-dom";


import { Products, Product, Login, MyCart } from "./";

const App = () => {
  const [products, setProducts] = useState([]);
  const [user, setUser] = useState("");
  const [loggedIn, setLoggedin] = useState(false);
  const [userId, setUserId] = useState("");
  const [token, setToken] = useState();
  const [myProducts, setMyProducts] = useState();
  const [cartItems, setCartItems] = useState([]);
  const [itemId, setItemId] = useState();
  console.log("From app", user);
  console.log('Carteitems....from app', cartItems)
  let navigate = useNavigate();

  return (
    <>
      <header>
        <h1 className="headerTitle">Bikes and Things</h1>
        <Link className={"title"} to="/">
          Home
        </Link>
        <Link to="/products">Products</Link>{" "}

        <Link to="/cart">Cart</Link>{" "}
        <Link className={token ? "ifLoggedOut" : ""} to="api/users/login">
          Login/Register
        </Link>
        <button
          className={token ? "" : "ifLoggedOut"}
          onClick={() => {
            localStorage.removeItem("token");
            setLoggedin(false);
            navigate("/");
            window.location.reload(false);
          }}
        >
          Log Out
        </button>
      </header>
      <Routes>
        <Route exact path="/" />
        <Route
          exact
          path="/products"
          element={
            <Products
              setCartItems={setCartItems}
              cartItems={cartItems}
              itemId={itemId}
              products={products}
              setItemId={setItemId}
              setProducts={setProducts}
              setMyProducts={setMyProducts}
              user={user}
              token={token}
            />
          }
        />
        <Route
          exact
          path="/products/:productId"
          element={<Product products={products} />}
        />
        <Route
          path="api/users/:method"
          element={
            <Login
              setToken={setToken}
              setLoggedin={setLoggedin}
              loggedIn={loggedIn}
              setUserId={setUserId}
              setUser={setUser}
            />
          }
        />
        <Route
          exact
          path="/cart"
          element={
            <MyCart
              userId={userId}
              token={token}
              cartItems={cartItems}
              products={products}
              myProducts={myProducts}
              setMyProducts={setMyProducts}
            />
          }
        />
      </Routes>
    </>
  );
};

export default App;
