import React, { useEffect, useState } from "react";

import { Link, Routes, useNavigate } from "react-router-dom";
import { createOrder, getCartByUser, getProductById } from "../api";



const Products = ({ products, setProducts, user, token, setMyProducts }) => {

  console.log(">>> products js component", products);




  const navigate = useNavigate();

  const allProducts = async () => {
    try {
      const response = await fetch(`http://localhost:3000/api/products`);
      const data = await response.json();
      setProducts(data);
    } catch (error) {
      console.error(error);
    }
  };
  useEffect(() => {
    allProducts();
  }, []);

  const addProductToOrder = async (productId) => {
    console.log("adding product to order");

    if(user.id){
      const order = await createOrder({
        userId: user.id,
        completePurchase: false,
        productId: productId,
        quantity: 1,
        token,
      });

      console.log("From addProdcct", order);
      const cart = await getCartByUser(user.id, token);
      console.log('>>>>???/',cart)
      setMyProducts(cart);
    } else {

      const product = await getProductById(productId)
      console.log('product>>>', product)
      const localCart = JSON.stringify(product)
      console.log('localCart>>>', localCart)
      localStorage.setItem('local-cart', localCart)
      setMyProducts(localCart)
    }
  };

  return (
    <>
      <h2>Products</h2>
      <div id='all-products-container'>
      {products.map((product) => (
        <div className="single-product-card" key={product.id}>
          <h1>{product.title}</h1>
          <h3>{'$'}{product.price}</h3>
          <p>{product.description}</p>
          <img src={product.imgurl}></img>
          <button
            value={product.id}
            onClick={(event) => navigate(`${event.target.value}`)}
          >
            View
          </button>
          <br></br>
          <button
            value={product.id}
            onClick={(event) => addProductToOrder(event.target.value)}
          >
            Add to Cart
          </button>
        </div>
      ))}
      </div>
    </>
  );
};

export default Products;
