import React, { useState, useEffect } from "react";

import { Routes, Route, Link, useNavigate } from "react-router-dom";
import { getCartByUser } from "../api";
import { getAllCheckoutsByUserId } from "../api";
import { Products, Product, Login, MyCart, AdminEdit } from "./";

const App = () => {
  const [products, setProducts] = useState([]);
  const [user, setUser] = useState("");
  const [loggedIn, setLoggedin] = useState(false);
  const [userId, setUserId] = useState("");
  const [token, setToken] = useState();
  const [myProducts, setMyProducts] = useState();
  const [cartItems, setCartItems] = useState([]);
  const [itemId, setItemId] = useState();
  const [checkout, setCheckout] = useState([]);
  const [isAdmin, setIsAdmin] = useState(false);
  const [userData, setUserData] = useState({});
  console.log("isadmin>>>>", isAdmin);
  console.log("Carteitems....from app", cartItems);
  let navigate = useNavigate();

  //   useEffect( async () => {
  //     setCartItems([]);
  //     if(userId){
  //         const usersCart = await getCartByUser(userId, token);
  //         if(usersCart){
  //             setCartItems(usersCart);
  //         }
  //     } else {
  //         let localCart = localStorage.getItem('temporary-cart');
  //         if(!localCart){
  //             localCart = [];
  //             localStorage.setItem('temporary-cart', localCart);
  //         }
  //         setCartItems(localCart);

  //     }
  // }, [userId]);

  useEffect(async () => {
    if (userData.id !== undefined) {
      const usersCheckout = await getAllCheckoutsByUserId(userData.id, token);
      setCheckouts(usersCheckout);
    }
  }, [userData]);

  return (
    <>
      <header>
        <h1 className="headerTitle">Bikes and Things</h1>
        <Link className={"title"} to="/">
          Home
        </Link>
        <Link to="/products">Products</Link> <Link to="/cart">Cart</Link>{" "}
        <Link className={token ? "ifLoggedOut" : ""} to="api/users/login">
          Login/Register
        </Link>
        <button
          className={token ? "" : "ifLoggedOut"}
          onClick={() => {
            localStorage.removeItem("token");
            setLoggedin(false);
            navigate("/");
            window.location.reload(false);
          }}
        >
          Log Out
        </button>
        {isAdmin ? <Link to="/admin">Admin</Link> : null}
      </header>
      <Routes>
        <Route exact path="/" />
        <Route
          exact
          path="/products"
          element={
            <Products
              isAdmin={isAdmin}
              setCartItems={setCartItems}
              cartItems={cartItems}
              itemId={itemId}
              products={products}
              setItemId={setItemId}
              setProducts={setProducts}
              setMyProducts={setMyProducts}
              user={user}
              token={token}
            />
          }
        />
        <Route
          exact
          path="/products/:productId"
          element={<Product products={products} isAdmin={isAdmin} />}
        />
        <Route
          path="api/users/:method"
          element={
            <Login
              setIsAdmin={setIsAdmin}
              setToken={setToken}
              setLoggedin={setLoggedin}
              loggedIn={loggedIn}
              setUserId={setUserId}
              setUser={setUser}
              setCartItems={setCartItems}
              userId={userId}
              token={token}
              user={user}
            />
          }
        />
        <Route
          exact
          path="/cart"
          element={
            <MyCart
              userId={userId}
              token={token}
              products={products}
              myProducts={myProducts}
              setMyProducts={setMyProducts}
              setCheckout={setCheckout}
              setCartItems={setCartItems}
              userData={userData}
            />
          }
        />
        <Route
          exact
          path="/admin"
          element={<AdminEdit products={products} token={token} />}
        />
      </Routes>
    </>
  );
};

export default App;
