import React, { useEffect, useState } from "react";

import { Link, Routes, useNavigate } from "react-router-dom";
import { destroyCart, getCartByUser, getAllCheckoutsByUserId } from "../api";

const MyCart = ({
  userId,
  token,
  setMyProducts,
  myProducts,
  products,
  setCheckout,
  setCartItems,
  userData,
}) => {
  let totalPrice = 0;

  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");
  const [street, setStreet] = useState("");
  const [city, setCity] = useState("");
  const [state, setState] = useState("");
  const [zip, setZip] = useState("");
  const [phone, setPhone] = useState("");
  const [creditCardNumber, setCreditCardNumber] = useState("");
  const [creditCardExp, setCreditCardExp] = useState("");
  const [creditValidationNumber, setCreditValidationNumber] = useState("");
  const [quantity, setQuantity] = useState();
  const [total, setTotal] = useState(0);
  console.log(">>>>>", total);

  const deleteCart = async () => {
    if (userId) {
      await destroyCart(userId, token);
      const usersCart = await getCartByUser(userId, token);
      setCartItems(usersCart);
    } else {
      const localCart = localStorage.setItem(
        "temporary-cart",
        JSON.strigify([])
      );
      setCartItems([]);
    }

    setTotal(0);
  };

  // const getCartQuantity = async () => {
  //   try {
  //     const response = await fetch(
  //       `http://localhost:3000/api/orders/cart/${userId}`
  //     );
  //     const data = await response.json();

  //     console.log('data from MyCart', data)

  //     for(var i = 0; i < data.length; i++){
  //       tempCart[data[i].title] = 1;
  //     }

  //     for(var i = 0; i < data.length; i++){
  //       tempCart[data[i].title] = tempCart[data[i].title] + 1;
  //     }

  //     console.log('tempCart',  tempCart);
  //     let tempArray = [];
  //     var tempCartKeys = Object.keys(tempCart);
  //     var tempCartValues = Object.values(tempCart);
  //     for(var i = 0; i < tempCartKeys.length; i++){
  //       var objKey = tempCartKeys[i];
  //       tempArray.push({
  //         [objKey]: tempCartValues[i]
  //       })
  //     }

  //     setMyCartQuantity(tempArray)
  //   } catch (error) {
  //     console.error(error);
  //   }
  // };
  // useEffect(() => {
  //    getCartQuantity();
  // }, []);

  function getTotal() {
    myProducts.map((element) => {
      totalPrice = Number(totalPrice) + Number(element.price);
      setTotal(totalPrice);
    });
  }
  const getMyProducts = async () => {
    try {
      const response = await fetch(
        `http://localhost:3000/api/orders/cart/${userId}`
      );
      const data = await response.json();
      setMyProducts(data);
      getTotal();
      setTotal(totalPrice);
    } catch (error) {
      console.error(error);
    }
  };
  useEffect(() => {
    getMyProducts();
  }, []);

  console.log("from my cart", myProducts);

  const submitOrder = async (event) => {
    event.preventDefault();

    // Logged in
    if (userData.id) {
      const checkout = await submitCheckout(
        {
          userId: userData.id,
          firstName,
          lastName,
          street,
          city,
          state,
          zip,
          phone,
          creditCardNumber,
          creditCardExp,
          creditValidationNumber,
          orders: cart,
        },
        token
      );

      const usersCart = await getCartByUser(userData.id, token);
      setCart(usersCart);
      const usersCheckout = await getAllCheckoutsByUserId(userData.id, token);
      setCheckout(usersCheckout);

      setSuccess(`Purchase was a success!`);
      handleClick();

      // Guest or logged out
    } else {
      const checkout = await submitGuestCheckout(
        {
          firstName,
          lastName,
          street,
          city,
          state,
          zip,
          phone,
          creditCardNumber,
          creditCardExp,
          creditValidationNumber,
          orders: cart,
        },
        token
      );

      const localCart = localStorage.setItem(
        "capstone-cart",
        JSON.stringify([])
      );
      setCart([]);

      setSuccess(`Purchase was a success!`);
      handleClick();
    }

    setTotalCartCost(0);
  };

  return (
    <>
      <h1>My Cart</h1>
      {myProducts ? (
        myProducts.map((item) => (
          <div className="cart" key={item.id}>
            <h5>
              {item.title} {"$"}
              {item.price} {item.orderQuantity} <button>+</button>{" "}
              <button>-</button>
            </h5>
          </div>
        ))
      ) : (
        <div>Cart is Empty</div>
      )}
      <h5>
        Total Cost: {"$"}
        {total}
      </h5>
      <button>Edit Cart</button>
      <button onClick={() => deleteCart()}>Delete Cart</button>

      <form onSubmit={submitOrder}>
        <h3 id="checkoutInstruction">
          Please Enter Your Shipping and Payment Information!
        </h3>
        <div id="creditCardSection">
          <input
            className={classes.input}
            type="text"
            placeholder="First"
            required
            value={firstName}
            onChange={(event) => setFirstName(event.target.value)}
          ></input>

          <input
            className={classes.input}
            type="text"
            placeholder="Last"
            required
            value={lastName}
            onChange={(event) => setLastName(event.target.value)}
          ></input>
        </div>
        <div id="creditCardSection">
          <input
            className={classes.input}
            type="text"
            placeholder="street"
            required
            value={street}
            onChange={(event) => setStreet(event.target.value)}
          ></input>

          <input
            className={classes.input}
            type="text"
            placeholder="City"
            required
            value={city}
            onChange={(event) => setCity(event.target.value)}
          ></input>

          <input
            className={classes.input}
            type="text"
            placeholder="State"
            required
            value={state}
            onChange={(event) => setState(event.target.value)}
          ></input>

          <input
            className={classes.input}
            type="text"
            placeholder="zip"
            required
            value={zip}
            onChange={(event) => setZip(event.target.value)}
          ></input>

          <input
            className={classes.input}
            type="text"
            placeholder="Phone"
            required
            value={phone}
            onChange={(event) => setPhone(event.target.value)}
          ></input>
        </div>
        <div id="creditCardSection">
          <input
            className={classes.input}
            type="text"
            placeholder="CC Number"
            required
            value={creditCardNumber}
            onChange={(event) => setCreditCardNumber(event.target.value)}
          ></input>

          <input
            className={classes.input}
            type="text"
            placeholder="Expiration"
            required
            value={creditCardExp}
            onChange={(event) => setCreditCardExp(event.target.value)}
          ></input>

          <input
            className={classes.input}
            type="text"
            placeholder="CVV# on Back"
            required
            value={creditValidationNumber}
            onChange={(event) => setCreditValidationNumber(event.target.value)}
          ></input>
        </div>
        <Button
          id="paymentSubmit"
          type="submit"
          variant="contained"
          className={classes.button}
        >
          Submit Order
        </Button>
      </form>
    </>
  );
};

export default MyCart;
