import React, { useEffect, useState } from "react";

import { Link, Routes, useNavigate } from "react-router-dom";
import {
  destroyCart,
  getCartByUser,
  getAllCheckoutByUserId,
  submitCheckout,
  orderDelete,
} from "../api";

const MyCart = ({
  userId,
  token,
  setMyProducts,
  myProducts,
  products,
  setCheckout,
  setCartItems,
  user,
  cart,
  setCart,
  setTotalCartCost,
  totalCartCost,
}) => {
  let totalPrice = 0;

  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");
  const [street, setStreet] = useState("");
  const [city, setCity] = useState("");
  const [state, setState] = useState("");
  const [zip, setZip] = useState("");
  const [phone, setPhone] = useState("");
  const [creditCardNumber, setCreditCardNumber] = useState("");
  const [creditCardExp, setCreditCardExp] = useState("");
  const [creditValidationNumber, setCreditValidationNumber] = useState("");
  const [quantity, setQuantity] = useState();
  const [success, setSuccess] = useState(false);
  const [open, setOpen] = useState(false);
  const [total, setTotal] = useState(0);

  const handleClick = () => {
    setOpen(true);
  };

  function getTotal() {
    if (myProducts.length > 0) {
      myProducts.map((element) => {
        totalPrice = Number(totalPrice) + Number(element.price);
        setTotal(totalPrice);
      });
    }
  }

  const getMyProducts = async () => {
    try {
      const response = await fetch(
        `http://localhost:3000/api/orders/cart/${userId}`
      );
      const data = await response.json();
      setMyProducts(data);
      getTotal();
      setTotal(totalPrice);
    } catch (error) {
      console.error(error);
    }
  };
  useEffect(() => {
    getMyProducts();
  }, []);

  const deleteCart = async () => {
    if (userId) {
      await destroyCart(userId, token);
      const usersCart = await getCartByUser(userId, token);
      setMyProducts(usersCart);
    }

    setTotal(0);
  };

  const submitOrder = async (event) => {
    event.preventDefault();

    // Logged in
    if (user.id) {
      const checkout = await submitCheckout(
        {
          userId: user.id,
          firstName,
          lastName,
          street,
          city,
          state,
          zip,
          phone,
          creditCardNumber,
          creditCardExp,
          creditValidationNumber,
          orders: cart,
        },
        token
      );
    }
    const usersCart = await getCartByUser(user.id, token);
    setMyProducts(usersCart);
    deleteCart();
    setSuccess(true);
    setFirstName("");
    setLastName("");
    setStreet("");
    setCity("");
    setState("");
    setZip("");
    setPhone("");
    setCreditValidationNumber("");
    setCreditCardExp("");
    setCreditCardNumber("");
  };

  const removeItem = async () => {
    if (userId) {
      const data = await orderDelete(cartItem.orderId, token);
      const usersCart = await getCartByUser(userId, token);
      setMyProducts([]);
      setMyProducts(usersCart);

      // setTotalCartCost(totalCartCost - totalCost);
      <button
        onClick={() => {
          removeItem();
        }}
      >
        Remove From Cart
      </button>;
    }
  };
  return (
    <>
      {user.id ? <h1>Welcome to your cart {user.firstName}</h1> : <h1>Cart</h1>}
      {myProducts ? (
        myProducts.map((item) => (
          <div className="cartItem" key={item.id}>
            <h5>
              {item.title} {"$"}
              {item.price}
              <select>
                <option>1</option>
                <option>2</option>
                <option>3</option>
                <option>4</option>
                <option>5</option>
                <option>6</option>
                <option>7</option>
                <option>8</option>
                <option>9</option>
                <option>10</option>
              </select>
            </h5>
          </div>
        ))
      ) : (
        <div>Cart is Empty</div>
      )}
      <h5>
        Total Cost: {"$"}
        {total}
      </h5>
      <button>Edit Cart</button>
      <button onClick={() => deleteCart()}>Delete Cart</button>

      <form onSubmit={submitOrder}>
        <h3 id="checkoutInstruction">
          Please Enter Your Shipping and Payment Information!
        </h3>
        <div id="creditCardSection">
          <input
            className="input"
            type="text"
            placeholder="First"
            required
            value={firstName}
            onChange={(event) => setFirstName(event.target.value)}
          ></input>

          <input
            className="input"
            type="text"
            placeholder="Last"
            required
            value={lastName}
            onChange={(event) => setLastName(event.target.value)}
          ></input>
        </div>
        <div id="creditCardSection">
          <input
            className="input"
            type="text"
            placeholder="Street"
            required
            value={street}
            onChange={(event) => setStreet(event.target.value)}
          ></input>

          <input
            className="input"
            type="text"
            placeholder="City"
            required
            value={city}
            onChange={(event) => setCity(event.target.value)}
          ></input>

          <input
            className="input"
            type="text"
            placeholder="State"
            required
            value={state}
            onChange={(event) => setState(event.target.value)}
          ></input>

          <input
            className="input"
            type="text"
            placeholder="Zip"
            required
            value={zip}
            onChange={(event) => setZip(event.target.value)}
          ></input>

          <input
            className="input"
            type="text"
            placeholder="Phone"
            required
            value={phone}
            onChange={(event) => setPhone(event.target.value)}
          ></input>
        </div>
        <div id="creditCardSection">
          <input
            className="input"
            type="text"
            placeholder="CC Number"
            required
            value={creditCardNumber}
            onChange={(event) => setCreditCardNumber(event.target.value)}
          ></input>

          <input
            className="input"
            type="text"
            placeholder="Expiration"
            required
            value={creditCardExp}
            onChange={(event) => setCreditCardExp(event.target.value)}
          ></input>

          <input
            className="input"
            type="text"
            placeholder="CVV# on Back"
            required
            value={creditValidationNumber}
            onChange={(event) => setCreditValidationNumber(event.target.value)}
          ></input>
        </div>
        {success ? (
          <div>Thank You for your purchase! Youre purchase is complete.</div>
        ) : null}
        <button
          id="paymentSubmit"
          type="submit"
          variant="contained"
          className="button"
        >
          Submit Order
        </button>
      </form>
    </>
  );
};

export default MyCart;
